import React, { Component } from "react";
export default class Porfolio extends Component {
  constructor(props) {
    super(props);
    this.toggleVisiblity = this.toggleVisiblity.bind(this);
    this.state = {
      visibility: false,
      projects: <p>here are the projects</p>
    };
  }

  toggleVisiblity() {
    this.setState(prevState => {
      return {
        visibility: !prevState.visibility
      };
    });
    console.log(this.state.visibility);

    // this.setState({ visibility: !this.state.visibility }); //old way
  }
  handleClick = e => {
    e.preventDefault();
    console.log("The link was clicked.");
    this.toggleVisiblity();
  };

  render() {
    let resumeData = this.props.resumeData;
    return (
      <section id="portfolio">
        <h1>Check Out Some of My Projects!</h1>
        <div className="portfolio__button__layout">
          <button onClick={this.handleClick}>Javascript</button>
          <button onClick={this.handleClick}>CSS & HTML</button>
          <button onClick={this.handleClick}>React</button>
          <button onClick={this.handleClick}>NodeJS</button>
          <button onClick={this.handleClick}>Ruby on Rails</button>
        </div>
        <div className="portfolioGrid">
          {resumeData.portfolio &&
            resumeData.portfolio.map(item => {
              return (
                <a
                  href={`${item.url}`}
                  rel="noopener noreferrer"
                  target="_blank"
                >
                  <div className="portfolio-item portfolioGridSquare">
                    <div className="item-wrap">
                      <img
                        src={`${item.imgurl}`}
                        alt="these are my projects"
                        className="item-img"
                      />
                      <div className="overlay">
                        <div className="portfolio-item-meta">
                          <h5>{item.name}</h5>
                          <p>{item.description}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                </a>
              );
            })}
          <div id="portfolio-wrapper"></div>
        </div>
      </section>
    );
  }
}
